    // Track used messages to prevent duplicates
    const usedMessages = {};

    // Function to get a random message from a category
    function getRandomMessage(category) {
        const messages = categories[category];
        if (!messages) return "Category not found.";
        const unusedMessages = messages.filter((message) => !usedMessages[message]);
        if (unusedMessages.length === 0) {
            // All messages have been used; reset the set
            for (const message of messages) {
                usedMessages[message] = false;
            }
            return "All messages from this category have been generated.";
        } else {
            const randomIndex = Math.floor(Math.random() * unusedMessages.length);
            const selectedMessage = unusedMessages[randomIndex];
            usedMessages[selectedMessage] = true;
            return selectedMessage;
        }
    }

    // Function to update the message with the fan's name
    function updateMessageWithFanName(message, fanName) {
        if (fanName === "") {
            return message.replace(/NAME/g, "");
        } else {
            return message.replace(/NAME/g, fanName);
        }
    }

    // Function to reset used messages
    function resetUsedMessages(category) {
        for (const message of categories[category]) {
            usedMessages[message] = false;
        }
    }

    // Generate and copy message for a category
    function generateAndCopyMessage(category, fanName, messageElement) {
        const randomMessage = getRandomMessage(category);
        if (randomMessage === "Category not found.") {
            messageElement.textContent = "Category not found.";
        } else if (randomMessage === "All messages from this category have been generated.") {
            messageElement.textContent = randomMessage;
            resetUsedMessages(category);
        } else {
            const updatedMessage = updateMessageWithFanName(randomMessage, fanName);
            messageElement.textContent = updatedMessage;
        }
    }

